import { auth } from '../firebase'
import React, { useLayoutEffect } from 'react'
import { ScrollView, View, Text, SafeAreaView } from 'react-native'
import { Avatar } from 'react-native-elements'
import CustomListItem from '../components/CustomListItem'
import { TouchableOpacity } from 'react-native'

const HomeScreen = ({navigation}) => {

    useLayoutEffect(() => {
        navigation.setOptions({
            title: "Signal",
            headerStyle: { backgroundColor: "#fff" },
            headerTitleStyle: { color: "black" },
            headerTintColor: "black",
            headerLeft: () => (
                <View style={{ marginLeft: 20 }}>
                    <TouchableOpacity>
                        <Avatar
                            rounded
                            source={{
                                uri: "https://scontent-ort2-2.xx.fbcdn.net/v/t1.0-9/139795578_3709492565794305_7976351734504873887_n.jpg?_nc_cat=109&ccb=2&_nc_sid=09cbfe&_nc_ohc=OiHPGuvLBOkAX8V0wbZ&_nc_ht=scontent-ort2-2.xx&oh=3d092662d98a8fd6834f2d2cd89be88c&oe=603B975C"
                            }}
                        />
                    </TouchableOpacity>
                </View>
            ),
        })
    }, [navigation])

    console.log(auth)

    return (
        <SafeAreaView>
            <ScrollView>
                <CustomListItem />
            </ScrollView>
        </SafeAreaView>
    )
}

export default HomeScreen
