        //     <StatusBar style="light" />
        //     <Text h3 style={{ marginBottom: 50 }}>
        //         Create a signal account
        //     </Text>
        //     <View style={styles.inputContainer}>

        //     </View>
        //     <Button style={styles.button} raised onPress={register} title="Register" />
