    )
}

export default RegisterScreen;

const styles = StyleSheet.create({
    container: {
        flex: 1,
        justifyContent: "center",
        alignItems: "center",
        padding: 10,
        backgroundColor:"white",
    },
    inputContainer: {
        width: 300,
    },
    button: {
        width: 200,
        marginTop:10
    }
})
